    boolean statusSoma = false;
    boolean statusSubtracao = false;
    boolean statusMultiplicacao = false;
    boolean statusDivisao = false;
    long soma=0;
    long subtracao=0;
    long multiplicacao=0;
    double divisao=0;
    double resultadoF=0;
    long resultadoI=0;
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        soma = Integer.parseInt(texto.getText());
        statusSoma=true;
        texto.setText("");
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        if(statusSoma == true){
            resultadoI = soma + Long.parseLong(texto.getText());
            texto.setText(Long.toString(resultadoI));
            statusSoma = false;
        }else if(statusSubtracao == true){
            resultadoI = subtracao - Long.parseLong(texto.getText());
            texto.setText(Long.toString(resultadoI));
            statusSubtracao = false;
        }else if(statusMultiplicacao == true){
            resultadoI = multiplicacao * Long.parseLong(texto.getText());
            texto.setText(Long.toString(resultadoI));
            statusMultiplicacao = false;
        }else if(statusDivisao == true){
            resultadoF = divisao / Double.parseDouble(texto.getText());
            texto.setText(Double.toString(resultadoF));
            statusDivisao = false;
        }
    }//GEN-LAST:event_jButton5ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        subtracao = Integer.parseInt(texto.getText());
        statusSubtracao = true;
        texto.setText("");
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        multiplicacao = Long.parseLong(texto.getText());
        statusMultiplicacao = true;
        texto.setText("");
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        divisao = Double.parseDouble(texto.getText());
        statusDivisao = true;
        texto.setText("");
    }//GEN-LAST:event_jButton4ActionPerformed

